mapper.select:
select cno, id, password, name, ceo, regDate, cStatus
from company
where cno = #{cno}

mapper.update:
update company
set name = #{name}, ceo = #{ceo}
where cno = #{cno}

mapper.select:
select cno, name
from company
where name like #{name}

mapper.insert:
update company
set cStatus = #{cStatus}
where cno = #{cno}

mapper.select:
select count(id)
from company
where id = #{id}

select bno, cno, bname, btel, post, address, manager, mtel, regDate as bregDate
from branch
where bno = #{bno}

select bno, cno, bname, btel, post, address, manager, mtel, regDate as bregDate
from branch
where bname like #{name}

mapper.insert:
update branch
set bname = #{bname}, btel = #{btel}, post= #{post}, manager = #{manager}, mtel = #{mtel}, address = #{address}
where bno = #{bno}

mapper.select:
select ano, cno, auth
from auth
where cno = #{cno}
