mapper.select:
select m.*, a.* from member m
left join auth a on m.mno = a.mno
where id = #{id}

mapper.update:
update work_management set endTime = now(), overTime = ${overTime}, endstatus = ${endstatus}
where wDay=date(now())
and endTime is null
and mno = #{mno}
and bno = #{bno}

mapper.select:
select ano, mno, auth
from auth
where mno = #{mno}
